// components/AppShell.jsx
import React from "react";
import styled from "styled-components";
import Header from "./Header";
import SideBar from "./SideBar";
import { Outlet } from "react-router-dom";

/* Thème minimal (seulement ce qu'on utilise) */
const BG = "#F5F7FA";

/* Layout */
const Shell = styled.div`
  min-height: 100vh;
  background: ${BG};
  display: grid;
  grid-template-rows: auto 1fr;
  grid-template-columns: 260px 1fr;
  grid-template-areas:
    "header header"
    "sidebar content";
`;

const HeaderWrap = styled.header`
  grid-area: header;
  position: sticky;
  top: 0;
  z-index: 10;
`;

const SidebarWrap = styled.aside`
  grid-area: sidebar;
  background: #ffffff;
  border-right: 1px solid #eef2f7;
  /* Assure une largeur cohérente même si SideBar ne la fixe pas */
  width: 260px;
  min-width: 260px;
`;

const Content = styled.main`
  grid-area: content;
  padding: 18px;
  overflow: auto;
`;

/* Carte simple pour donner une base clean aux pages
   (si une page gère déjà sa propre carte, ce wrapper reste neutre) */
const ContentCard = styled.div`
  background: #fff;
  border-radius: 16px;
  box-shadow: 0 10px 26px rgba(16, 24, 40, 0.08);
  padding: 18px;
  min-height: calc(100vh - 18px - 64px); /* hauteur raisonnable sous le header */
`;

/* AppShell */
export default function AppShell() {
  return (
    <Shell>
      <HeaderWrap>
        <Header />
      </HeaderWrap>

      <SidebarWrap>
        <SideBar />
      </SidebarWrap>

      <Content>
        {/* Si vos pages ont déjà leur propre carte, vous pouvez
           remplacer <ContentCard> par <React.Fragment>. */}
        <ContentCard>
          <Outlet />
        </ContentCard>
      </Content>
    </Shell>
  );
}
