import React, { useState } from "react";
import styled from "styled-components";

const API = process.env.REACT_APP_API_URL || "http://localhost:5000";

const Wrap = styled.div`max-width: 720px; margin: 0 auto;`;
const H1 = styled.h1`font-size: 28px; margin-bottom: 10px;`;
const Grid = styled.form`display: grid; gap: 12px; grid-template-columns: 1fr 1fr;`;
const Full = styled.div`grid-column: 1 / -1;`;
const Label = styled.label`font-weight: 700; font-size: 14px;`;
const Input = styled.input`
  width: 100%; padding: 10px 12px; border:1px solid #d1d5db; border-radius:8px;
  &:focus{border-color:#D71A28; box-shadow:0 0 0 4px rgba(215,26,40,.12); outline:none;}
`;
const Textarea = styled.textarea`
  width:100%; min-height:140px; padding:12px; border:1px solid #d1d5db; border-radius:8px;
  &:focus{border-color:#D71A28; box-shadow:0 0 0 4px rgba(215,26,40,.12); outline:none;}
`;
const Row = styled.div`display:flex; gap:10px; justify-content:flex-end;`;
const Btn = styled.button`
  padding:10px 14px; border:0; border-radius:10px; font-weight:700; cursor:pointer;
  ${({variant})=>variant==="primary"
    ? "background:#D71A28;color:#fff; &:hover{background:#b91622;}"
    : "background:#e5e7eb;color:#111827; &:hover{background:#d1d5db;}"}
`;

export default function Contact() {
  const [form, setForm] = useState({ name:"", email:"", subject:"", message:"" });
  const [ok, setOk] = useState(""); const [err, setErr] = useState(""); const [loading, setLoading]=useState(false);

  const submit = async (e)=>{
    e.preventDefault(); setErr(""); setOk(""); setLoading(true);
    try{
      const res = await fetch(`${API}/api/contact`, {
        method:"POST",
        headers:{ "Content-Type":"application/json" },
        body: JSON.stringify(form)
      });
      const data = await res.json().catch(()=>({}));
      if(!res.ok) throw new Error(data.message || "Envoi impossible");
      setOk("Message envoyé. Nous vous répondrons rapidement.");
      setForm({ name:"", email:"", subject:"", message:"" });
    }catch(e){ setErr(e.message); }
    finally{ setLoading(false); }
  };

  return (
    <Wrap>
      <H1>Contact</H1>
      <p>Besoin d’aide ? Écrivez-nous à <a href="mailto:support@votredomaine.com">support@votredomaine.com</a></p>

      <Grid onSubmit={submit}>
        <div>
          <Label>Nom *</Label>
          <Input value={form.name} onChange={e=>setForm({...form, name:e.target.value})} required />
        </div>
        <div>
          <Label>Email *</Label>
          <Input type="email" value={form.email} onChange={e=>setForm({...form, email:e.target.value})} required />
        </div>
        <Full>
          <Label>Objet *</Label>
          <Input value={form.subject} onChange={e=>setForm({...form, subject:e.target.value})} required />
        </Full>
        <Full>
          <Label>Message *</Label>
          <Textarea value={form.message} onChange={e=>setForm({...form, message:e.target.value})} required />
        </Full>
        {err && <Full><div style={{color:"#7a2328", background:"#fde2e4", border:"1px solid #f9c6ca", borderRadius:8, padding:"10px 12px"}}>{err}</div></Full>}
        {ok && <Full><div style={{color:"#065f46", background:"#d1fae5", border:"1px solid #a7f3d0", borderRadius:8, padding:"10px 12px"}}>{ok}</div></Full>}
        <Full>
          <Row>
            <Btn type="reset" onClick={()=>setForm({name:"",email:"",subject:"",message:""})}>Annuler</Btn>
            <Btn variant="primary" disabled={loading}>{loading?"Envoi…":"Envoyer"}</Btn>
          </Row>
        </Full>
      </Grid>
    </Wrap>
  );
}
