import React, { useState } from "react";
import styled from "styled-components";
import { NavLink, useLocation, useNavigate } from "react-router-dom";
import { useAuth } from "../auth/AuthProvider";
import AdminLoginModal from "../auth/AdminLoginModal";

/* Palette */
const RED   = "#D71A28";
const INK   = "#111827";   // noir lisible
const GRAY  = "#6b7280";

/* Layout */
const Aside = styled.aside`
  height: 100%;
  padding: 16px 14px;
  background: transparent;
  display: flex;
  flex-direction: column;
`;

const Title = styled.div`
  font-size: 11px;
  font-weight: 900;
  color: ${GRAY};
  letter-spacing: .14em;
  text-transform: uppercase;
  margin: 6px 6px 12px;
`;

const Stack = styled.div`
  display: flex;
  flex-direction: column;
  gap: 14px;
`;

/* Base style – typographie moderne */
const itemBase = `
  display: flex;
  align-items: center;
  gap: 10px;
  min-height: 48px;
  padding: 12px 16px;
  border-radius: 16px;
  text-decoration: none;
  border: 1px solid #eef2f7;
  box-shadow: 0 6px 16px rgba(16,24,40,.06);
  transition: transform .08s ease, box-shadow .15s ease, border-color .2s ease, background .2s ease;

  /* typo moderne & attirante */
  font-weight: 800;
  letter-spacing: .2px;
  color: ${INK};
  font-size: 14px;

  &:hover {
    transform: translateX(3px);
    box-shadow: 0 12px 24px rgba(16,24,40,.08);
    border-color: #e5e7eb;
  }

  &.active {
    background: #fff;
    border-color: #e5e7eb;
  }
`;

/* Items normaux : pill “neumorphique” léger */
const ButtonItem = styled(NavLink)`
  ${itemBase};
  background: linear-gradient(180deg, #f8fafc 0%, #f3f4f6 100%);
`;

/* Bouton Admin : élégant, pas rouge foncé */
const AdminButton = styled.button`
  ${itemBase};
  cursor: pointer;

  /* fond rouge CLAIR en dégradé + bord doux */
  background: linear-gradient(180deg, #ffe7ea 0%, #ffdfe2 100%);
  border-color: #f3c7cd;
  box-shadow: 0 8px 18px rgba(215, 26, 40, .12);

  &:hover {
    box-shadow: 0 14px 26px rgba(215, 26, 40, .18);
    background: linear-gradient(180deg, #ffeef0 0%, #ffe3e6 100%);
  }

  &.active {
    background: linear-gradient(180deg, #ffe0e3 0%, #ffd8dc 100%);
    border-color: #eb9fa8;
  }

  /* petit dot rouge pour le look */
  &::before {
    content: "";
    width: 10px;
    height: 10px;
    border-radius: 999px;
    background: ${RED};
    box-shadow: 0 0 0 3px rgba(215,26,40,.14);
  }
`;

export default function SideBar() {
  const { user } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();

  const [showAdminModal, setShowAdminModal] = useState(false);
  const isAdminActive = location.pathname.startsWith("/admin");

  const onAdminClick = () => {
    if (user?.role === "admin") navigate("/admin");
    else setShowAdminModal(true);
  };

  return (
    <Aside>
      <Title>Navigation</Title>

      <Stack>
        <ButtonItem to="/documents">Documents</ButtonItem>
        <ButtonItem to="/workflows">Workflows</ButtonItem>
        <ButtonItem to="/library">Library</ButtonItem>

        <AdminButton
          type="button"
          onClick={onAdminClick}
          className={isAdminActive ? "active" : undefined}
        >
          Admin
        </AdminButton>
      </Stack>

      {showAdminModal && (
        <AdminLoginModal onClose={() => setShowAdminModal(false)} />
      )}
    </Aside>
  );
}
