// components/Footer.jsx
import React from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";

const FooterBar = styled.footer`
  background: #ffffff;
  border-top: 1px solid #eef2f7;

  /* Aligne le footer sur la grille (spacer 260px pour la sidebar) */
  display: grid;
  grid-template-columns: 260px 1fr;

  @media (max-width: 960px) {
    grid-template-columns: 1fr; /* pas de spacer sur mobile */
  }
`;

const Inner = styled.div`
  grid-column: 2;                 /* place le contenu dans la colonne “contenu” */
  padding: 14px 18px;
  display: flex;
  align-items: center;
  justify-content: space-between;
  color: #6b7280;
  font-size: 13px;
`;

const Brand = styled.div`
  display: flex;
  align-items: center;
  gap: 10px;

  img { height: 30px; width: auto; }

  strong { color: #111827; font-weight: 700; }
`;

const Nav = styled.nav`
  display: flex;
  gap: 16px;
`;

const FooterLink = styled(Link)`
  color: #6b7280;
  text-decoration: none;
  &:hover { color: #111827; text-decoration: underline; }
`;

export default function Footer() {
  const year = new Date().getFullYear();

  return (
    <FooterBar>
      <Inner>
        <Brand>
          <img src="/redal.png" alt="Redal" />
          <strong>Digital Signature</strong>
          <span>© {year}</span>
        </Brand>

        <Nav>
          <FooterLink to="/legal">Mentions légales</FooterLink>
          <FooterLink to="/privacy">Confidentialité</FooterLink>
          <FooterLink to="/contact">Contact</FooterLink>
        </Nav>
      </Inner>
    </FooterBar>
  );
}
