import React, { useEffect, useState } from "react";
import styled from "styled-components";
import { useAuth } from "./AuthProvider";
import { useNavigate } from "react-router-dom";

/* ---- Thème ---- */
const RED  = "#D71A28";
const GRAY = "#6b7280";

/* ---- UI ---- */
const Backdrop = styled.div`
  position: fixed;
  inset: 0;
  background: rgba(15, 23, 42, .55);     /* garde le fond de Documents visible */
  backdrop-filter: blur(2px);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
`;

const Card = styled.form`
  width: 100%;
  max-width: 460px;
  background: #fff;
  border-radius: 16px;
  padding: 28px;
  box-shadow: 0 20px 40px rgba(16,24,40,.18);
  position: relative;
`;

const Close = styled.button`
  position: absolute;
  top: 10px;
  right: 12px;
  border: 0;
  background: transparent;
  color: ${GRAY};
  font-size: 20px;
  cursor: pointer;
`;

const Title = styled.h3`
  margin: 0 0 6px;
  text-align: center;
  font-size: 22px;
  font-weight: 800;
  color: #111827;
`;

const Sub = styled.p`
  margin: 0 0 16px;
  text-align: center;
  color: ${GRAY};
  font-size: 14px;
`;

const Label = styled.label`
  display: block;
  margin: 12px 0 6px;
  font-size: 14px;
  font-weight: 600;
  color: #111827;
`;

const Input = styled.input`
  width: 100%;
  padding: 12px 14px;
  border: 1px solid #d1d5db;
  border-radius: 8px;
  font-size: 15px;
  outline: none;
  transition: box-shadow .2s, border-color .2s;
  &:focus {
    border-color: ${RED};
    box-shadow: 0 0 0 4px rgba(215, 26, 40, .12);
  }
`;

const Error = styled.div`
  margin-top: 10px;
  color: #7a2328;
  background: #fde2e4;
  border: 1px solid #f9c6ca;
  border-radius: 8px;
  padding: 10px 12px;
  font-size: 14px;
  text-align: center;
`;

const Row = styled.div`
  display: flex;
  gap: 12px;
  margin-top: 18px;
`;

const Button = styled.button`
  flex: 1;
  padding: 12px 14px;
  border: 0;
  border-radius: 10px;
  font-weight: 700;
  cursor: pointer;
  transition: transform .04s ease, background .15s ease, opacity .15s;
  ${({ variant }) =>
    variant === "primary"
      ? `background: ${RED}; color: #fff; &:hover{background:#b91622;}`
      : `background: #e5e7eb; color:#111827; &:hover{background:#d1d5db;}`}
  &:active { transform: translateY(1px); }
  &:disabled { opacity: .6; cursor: not-allowed; }
`;

/* ---- Composant ---- */
export default function AdminLoginModal({ onClose }) {
  const navigate = useNavigate();
  const { login, isAdmin, logout } = useAuth();   // ⚠️ on utilise bien login() puis isAdmin()
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  // ESC pour fermer
  useEffect(() => {
    const onKey = (e) => e.key === "Escape" && onClose?.();
    window.addEventListener("keydown", onKey);
    return () => window.removeEventListener("keydown", onKey);
  }, [onClose]);

  const handleSubmit = async (e) => {
    e?.preventDefault();
    setError("");
    setLoading(true);
    try {
      await login({ email, password });        // AuthProvider → setUser
      if (!isAdmin()) {
        setError("Accès refusé : compte non administrateur.");
        logout();                               // optionnel : on nettoie la session non-admin
        return;
      }
      navigate("/admin");
      onClose?.();
    } catch (err) {
      let msg = "Échec de connexion";
      try { const j = JSON.parse(err.message); msg = j.message || msg; } catch {}
      setError(msg);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Backdrop onClick={onClose}>
      <Card onClick={(e) => e.stopPropagation()} onSubmit={handleSubmit}>
        <Close type="button" aria-label="Close" onClick={onClose}>×</Close>
        <Title>Admin Login</Title>
        <Sub>Access restricted to administrators</Sub>

        <Label>Email *</Label>
        <Input
          type="email"
          placeholder="name@example.com"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          autoFocus
          required
        />

        <Label>Password *</Label>
        <Input
          type="password"
          placeholder="••••••••"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />

        {error && <Error>{error}</Error>}

        <Row>
          <Button type="button" variant="secondary" onClick={onClose}>Cancel</Button>
          <Button type="submit" variant="primary" disabled={loading}>
            {loading ? "Logging in..." : "Login"}
          </Button>
        </Row>
      </Card>
    </Backdrop>
  );
}
