import React, { useState } from "react";
import styled from "styled-components";
import { NavLink, useLocation, useNavigate } from "react-router-dom";
import { useAuth } from "../auth/AuthProvider";
import AdminLoginModal from "../auth/AdminLoginModal";

const RED  = "#D71A28";
const INK  = "#111827";  // noir lisible
const GRAY = "#6b7280";

const Aside = styled.aside`
  height: 100%;
  padding: 16px 14px;
  background: transparent;
  display: flex;
  flex-direction: column;
`;

const Title = styled.div`
  font-size: 11px;
  font-weight: 900;
  color: ${GRAY};
  letter-spacing: .14em;
  text-transform: uppercase;
  margin: 6px 6px 12px;
`;

const Stack = styled.div`
  display: flex;
  flex-direction: column;
  gap: 12px;
`;

/* Style commun (même “écriture” pour tous) – texte NOIR */
const baseButtonCss = `
  display: flex;
  align-items: center;
  gap: 10px;
  padding: 12px 14px;
  border-radius: 14px;
  background: #fff;
  border: 1px solid #eef2f7;
  box-shadow: 0 6px 16px rgba(16, 24, 40, 0.06);
  text-decoration: none;
  transition: transform .08s ease, box-shadow .15s ease, border-color .2s ease;

  &:hover {
    transform: translateX(2px);
    box-shadow: 0 10px 20px rgba(16,24,40,.08);
  }

  &.active {
    border-color: #e5e7eb;
    background: #f9fafb;
  }

  span {
    font-weight: 900;
    letter-spacing: .3px;
    font-size: 14px;
    color: ${INK};             /* <- TEXTE NOIR visible */
  }
`;

/* Liens gris (Documents / Workflows / Library) */
const ButtonItem = styled(NavLink)`
  ${baseButtonCss}
`;

/* Bouton Admin – fond ROUGE, texte NOIR */
const AdminButton = styled.button`
  ${baseButtonCss}
  cursor: pointer;

  background: ${RED};
  border-color: #efc7cb;
  box-shadow: 0 6px 16px rgba(215, 26, 40, .12);

  &:hover {
    box-shadow: 0 10px 22px rgba(215, 26, 40, .22);
  }

  &.active {
    background: #b91622;
    border-color: #d89aa0;
  }

  /* force le noir même sur fond rouge */
  span { color: ${INK}; }
`;

export default function SideBar() {
  const { user } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();

  const [showAdminModal, setShowAdminModal] = useState(false);
  const isAdminActive = location.pathname.startsWith("/admin");

  const onAdminClick = () => {
    if (user?.role === "admin") navigate("/admin");
    else setShowAdminModal(true);
  };

  return (
    <Aside>
      <Title>Navigation</Title>
      <Stack>
        <ButtonItem to="/documents"><span>Documents</span></ButtonItem>
        <ButtonItem to="/workflows"><span>Workflows</span></ButtonItem>
        <ButtonItem to="/library"><span>Library</span></ButtonItem>

        <AdminButton
          type="button"
          onClick={onAdminClick}
          className={isAdminActive ? "active" : undefined}
        >
          <span>Admin</span>
        </AdminButton>
      </Stack>

      {showAdminModal && (
        <AdminLoginModal onClose={() => setShowAdminModal(false)} />
      )}
    </Aside>
  );
}
