import React, { useEffect, useState, useCallback } from "react";
import styled from "styled-components";

const API = process.env.REACT_APP_API_URL || "http://localhost:5000";

/* Styles */
const Table = styled.table`
  width: 100%;
  border-collapse: collapse;
  margin: 20px 0;
`;
const Th = styled.th`
  text-align: left;
  padding: 12px;
  background: #eee;
  border-bottom: 2px solid #ccc;
`;
const Td = styled.td`
  padding: 12px;
  border-bottom: 1px solid #ddd;    /* sans guillemets */
`;
const ToggleButton = styled.button`
  background-color: #007bff;        /* sans guillemets */
  color: #fff;
  border: none;
  border-radius: 4px;
  padding: 4px 10px;
  cursor: pointer;
`;
const DetailRow = styled.tr`
  background: #f9f9f9;              /* sans guillemets */
`;

export default function UserTable() {
  const [users, setUsers] = useState([]);
  const [expanded, setExpanded] = useState({});
  const [loading, setLoading] = useState(false);
  const [err, setErr] = useState("");

  const load = useCallback(async () => {
    setLoading(true);
    setErr("");
    try {
      const res = await fetch(`${API}/api/users`, { headers: { "Cache-Control": "no-cache" } });
      const data = await res.json();
      setUsers(Array.isArray(data) ? data : []);
    } catch (e) {
      console.error(e);
      setErr("Impossible de charger la liste des utilisateurs");
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => { load(); }, [load]);

  useEffect(() => {
    const onFocus = () => load();
    window.addEventListener("focus", onFocus);
    return () => window.removeEventListener("focus", onFocus);
  }, [load]);

  const toggleDetails = (id) => setExpanded((p) => ({ ...p, [id]: !p[id] }));

  const statusOptions = ["Pending", "Approved", "Archived"];
  const changeStatus = async (id) => {
    const u = users.find((x) => x.id === id);
    if (!u) return;
    const next = statusOptions[(statusOptions.indexOf(u.status) + 1) % statusOptions.length];

    try {
      const res = await fetch(`${API}/api/users/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ status: next }),
      });
      if (!res.ok) throw new Error();
      setUsers((prev) => prev.map((x) => (x.id === id ? { ...x, status: next } : x)));
    } catch (e) {
      alert("Mise à jour du statut échouée");
    }
  };

  return (
    <>
      <div style={{ display: "flex", gap: 8, alignItems: "center" }}>
        <h2 style={{ marginRight: "auto" }}>Users</h2>
        <button onClick={load} disabled={loading}>{loading ? "Refreshing..." : "Refresh"}</button>
      </div>

      {err && <div style={{ color: "#b91c1c", marginBottom: 10 }}>{err}</div>}

      <Table>
        <thead>
          <tr>
            <Th>ID</Th><Th>Name</Th><Th>Email</Th><Th>Status</Th><Th>Actions</Th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <React.Fragment key={user.id}>
              <tr>
                <Td>{user.id}</Td>
                <Td>{user.name}</Td>
                <Td>{user.email}</Td>
                <Td>{user.status}</Td>
                <Td>
                  <ToggleButton onClick={() => toggleDetails(user.id)}>Details</ToggleButton>{" "}
                  <ToggleButton onClick={() => changeStatus(user.id)}>Change status</ToggleButton>
                </Td>
              </tr>
              {expanded[user.id] && (
                <DetailRow>
                  <Td colSpan="5"><strong>Job Title:</strong> {user.jobTitle}</Td>
                </DetailRow>
              )}
            </React.Fragment>
          ))}
          {!users.length && !loading && (
            <tr><Td colSpan="5" style={{ color: "#6b7280" }}>No users</Td></tr>
          )}
        </tbody>
      </Table>
    </>
  );
}
