// components/AppShell.jsx
import React from "react";
import styled from "styled-components";
import { NavLink, Outlet } from "react-router-dom";
import { useAuth } from "../auth/AuthProvider";

/* ======= Thème ======= */
const RED   = "#D71A28";
const INK   = "#111827";
const MUTED = "#6b7280";
const BG    = "#F5F7FA";

/* ======= Layout ======= */
const Shell = styled.div`
  min-height: 100vh;
  background: ${BG};
  display: grid;
  grid-template-columns: 260px 1fr;
  grid-template-rows: auto 1fr;
  grid-template-areas:
    "sidebar header"
    "sidebar main";
`;

const Sidebar = styled.aside`
  grid-area: sidebar;
  background: #ffffff;
  border-right: 1px solid #eef2f7;
  padding: 18px 16px;
  display: flex;
  flex-direction: column;
  gap: 14px;
`;

const Brand = styled.div`
  font-size: 22px;
  font-weight: 800;
  letter-spacing: .3px;
  color: ${INK};
  margin-bottom: 6px;
  display: flex; align-items: center; gap: 10px;
  &:before{
    content:"";
    display:inline-block;
    width:10px;height:10px;border-radius:999px;
    background:${RED};
    box-shadow: 0 0 0 6px rgba(215,26,40,.12);
  }
`;

const Nav = styled.nav`
  display: grid;
  gap: 6px;
`;

const NavItem = styled(NavLink)`
  padding: 10px 12px;
  border-radius: 10px;
  color: ${INK};
  text-decoration: none;
  font-weight: 600;
  font-size: 14px;
  border: 1px solid transparent;
  &:hover { background:#f8fafc; }
  &.active{
    background: #fff1f2;
    border-color: #ffe4e6;
    color: ${RED};
  }
`;

const Header = styled.header`
  grid-area: header;
  background: #ffffffaa;
  backdrop-filter: blur(4px);
  border-bottom: 1px solid #eef2f7;
  display: flex;
  align-items: center;
  gap: 14px;
  padding: 12px 18px;
`;

const Spacer = styled.div`flex:1;`;

const UserChip = styled.div`
  display: inline-flex; align-items: center; gap: 10px;
  background: #f3f4f6; padding: 6px 10px; border-radius: 999px; color: ${INK};
  font-weight: 600; font-size: 14px;
`;

const Avatar = styled.div`
  width: 28px; height: 28px; border-radius: 999px; background: #fee2e2; color: ${RED};
  display: grid; place-items: center; font-weight: 800; font-size: 12px;
`;

const LogoutBtn = styled.button`
  margin-left: 8px;
  padding: 8px 10px;
  border: 0; border-radius: 10px;
  background: ${RED}; color:#fff; font-weight: 700; cursor: pointer;
  transition: background .15s ease, transform .04s ease;
  &:hover{ background:#b91622; } &:active{ transform: translateY(1px); }
`;

/* ======= SignNow controls (dans le header) ======= */
const BarForm = styled.form`
  display: inline-flex; gap: 8px; align-items: center;
  background:#fff; padding: 8px; border-radius: 12px; border:1px solid #eef2f7;
`;
const BarInput = styled.input`
  width: 200px;
  padding: 8px 10px; border: 1px solid #e5e7eb; border-radius: 8px; font-size: 13px;
  outline: none; transition: box-shadow .2s, border-color .2s;
  &:focus{ border-color:${RED}; box-shadow:0 0 0 4px rgba(215,26,40,.12); }
`;
const BarButton = styled.button`
  padding: 9px 12px; border:0; border-radius:10px; background:${RED}; color:#fff; font-weight:700;
  cursor:pointer; transition: background .15s ease, transform .04s ease;
  &:hover{ background:#b91622; } &:active{ transform: translateY(1px); }
`;

/* ======= Contenu ======= */
const Main = styled.main`
  grid-area: main;
  padding: 18px;
`;
const ContentCard = styled.div`
  background:#fff; border-radius:16px; box-shadow:0 10px 26px rgba(16,24,40,.08);
  padding: 18px;
`;

/* ======= Composant ======= */
export default function AppShell() {
  const { user, logout } = useAuth();

  const initials = (user?.name || user?.email || "U")
    .split(" ")
    .map(s => s[0]?.toUpperCase())
    .slice(0,2)
    .join("");

  const handleToken = (e) => {
    e.preventDefault();
    // TODO: branche ton appel de token ici
    // alert('Token requested!');
  };

  return (
    <Shell>
      {/* Sidebar */}
      <Sidebar>
        <Brand>DocSign</Brand>
        <Nav>
          <NavItem to="/documents">Documents</NavItem>
          <NavItem to="/workflows">Workflows</NavItem>
          <NavItem to="/library">Library</NavItem>
          <NavItem to="/admin">Admin</NavItem>
        </Nav>
      </Sidebar>

      {/* Header */}
      <Header>
        <BarForm onSubmit={handleToken}>
          <BarInput placeholder="SignNow Email" type="email" />
          <BarInput placeholder="SignNow Password" type="password" />
          <BarButton type="submit">Get Token</BarButton>
        </BarForm>

        <Spacer />

        <UserChip title={user?.email}>
          <Avatar>{initials}</Avatar>
          {user?.name || user?.email}
          <LogoutBtn onClick={logout}>Logout</LogoutBtn>
        </UserChip>
      </Header>

      {/* Main content (pages) */}
      <Main>
        <ContentCard>
          <Outlet />
        </ContentCard>
      </Main>
    </Shell>
  );
}
