import React, { useState } from "react";
import styled from "styled-components";
import { useAuth } from "./AuthProvider";
import { useNavigate } from "react-router-dom";

// Logo en background (pas de <img>)
const Logo = styled.div`
  position: fixed;
  top: 16px;
  left: 16px;
  width: 160px;           /* ajuste la largeur du logo */
  height: 50px;           /* ajuste la hauteur du logo */
  background-image: url("/redal.png");  /* depuis public/ */
  background-repeat: no-repeat;
  background-position: center left;
  background-size: contain;
  z-index: 999999;        /* au-dessus de tout */
  pointer-events: none;   /* ne bloque pas les clics */
`;

const Page = styled.div`
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  background: #f4f6f8;
  position: relative;
`;

const Card = styled.form`
  width: 100%;
  max-width: 400px;
  background: #fff;
  border-radius: 10px;
  padding: 32px;
  box-shadow: 0 4px 16px rgba(0,0,0,0.1);
`;

const Title = styled.h2`
  text-align: center;
  margin-bottom: 20px;
  color: #333;
`;

const Input = styled.input`
  width: 100%;
  padding: 12px;
  margin-bottom: 16px;
  border: 1px solid #ccd1d6;
  border-radius: 6px;
  font-size: 14px;
  &:focus { outline: none; border-color: #007bff; box-shadow: 0 0 0 3px rgba(0,123,255,.15); }
`;

const Button = styled.button`
  width: 100%;
  padding: 12px;
  background: #007bff;
  color: #fff;
  border: 0;
  border-radius: 6px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  &:hover { background: #0056b3; }
`;

const ErrorMessage = styled.div`
  color: #721c24;
  background: #f8d7da;
  border: 1px solid #f5c6cb;
  border-radius: 6px;
  padding: 10px;
  margin-bottom: 16px;
  font-size: 14px;
`;

const LoginForm = () => {
  const { login } = useAuth();
  const navigate = useNavigate();
  const [form, setForm] = useState({ email: "", password: "" });
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    try {
      await login(form);
      navigate("/");
    } catch (err) {
      setError(err.message || "Échec de connexion");
    }
  };

  return (
    <>
      <Logo /> {/* ← Logo en haut-gauche, toujours visible */}
      <Page>
        <Card onSubmit={handleSubmit}>
          <Title>Welcome Back</Title>
          <label>Email *</label>
          <Input
            type="email"
            placeholder="Enter your Email"
            value={form.email}
            onChange={(e) => setForm({ ...form, email: e.target.value })}
            required
          />
          <label>Password *</label>
          <Input
            type="password"
            placeholder="Enter your Password"
            value={form.password}
            onChange={(e) => setForm({ ...form, password: e.target.value })}
            required
          />
          {error && <ErrorMessage>{error}</ErrorMessage>}
          <Button type="submit">Login</Button>
        </Card>
      </Page>
    </>
  );
};

export default LoginForm;
